import React from "react";
import Header from "./Header";
import Order from "./Order";
import Inventory from "./Inventory";
import sampleFishes from "../sample-fishes";
class App extends React.Component {
  state = {
    fishes: {},
    order: {},
  };
  addFish = (fish) => {
    // take the older data of the state
    const fishes = { ...this.state.fishes };
    // include the new value into the state
    fishes[`fish${Date.now()}`] = fish;
    // update the state with setState API
    this.setState({ fishes });
  };
  loadSampleFishes = () => {
    // take a copy of state
    const fishes = { ...this.state.fishes };
    // update sample fishes with the state
    this.setState({ fishes: sampleFishes });
  };
  render() {
    const fishObjKeys = Object.keys(this.state.fishes);
    return (
      <div className="catch-of-the-day">
        <div className="menu">
          <Header tagline="Fresh seafood market" />
          <ul className="list-of-fishes">
            {fishObjKeys.forEach((key) => (<li className="menu-fish">
                <img
                  src="https://upload.wikimedia.org/wikipedia/commons/thumb/b/b6/Image_created_with_a_mobile_phone.png/1200px-Image_created_with_a_mobile_phone.png"
                  alt=""
                />
                <h3 className="fish-name">
                  Halibic something
                  <span className="price">$29.234</span>
                </h3>
                <p>
                  Lorem ipsum dolor sit amet, consectetur adipisicing elit. Qui,
                  cupiditate.
                </p>
                <button>Add to order</button>
              </li>) )}
          </ul>
        </div>
        <Order />
        <Inventory
          addFish={this.addFish}
          loadSampleFishes={this.loadSampleFishes}
        />
      </div>
    );
  }
}

export default App;
