import React from 'react';
import Header from './Header';
import Inventory from './Inventory';
import Order from './Order';

class App extends React.Component {
  // Fish and order state
  state = {
    fishes: {},
    order: {}
  }
  //Get new fish item from AddFishForm component
  addFish = (fish) => {
    //Collect old data of state
    const fishes = {...this.state.fishes};
    //Add new fish into fishes object
    fishes[`fish${Date.now()}`] = fish;
    //Update the new fish item into the state
    this.setState({fishes})
  }

  loadSampleFishs = fish => {
    //load previous data from the state
    const fishes = {...this.state.fishes};
    //push sample fishes into new object
    this.setState({fishes, fish});
  }
  render() {
    return (
      <div className='catch-of-the-day'>
        <div className='menu'>
          <Header tagline='Fajle is cool'/>
        </div>
        <Order />
        <Inventory loadSampleFishs={this.loadSampleFishs} addFish={this.addFish} />
      </div>
    );
  }
}

export default App;
