import React from "react";
import { formatPrice } from "../helpers";

class Order extends React.Component {
  render() {
    // get order ids array from order object
    const orderIds = Object.keys(this.props.order);
    // loop (reduce) over the order ids to get each fish total price
    const total = orderIds.reduce((prevTotal, key) => {
      // get fish from the order id
      const fish = this.props.fishes[key];
      // get order count from the order by order id
      const count = this.props.order;
      // check is the fish and fish status available
      const isAvailable = fish && fish.status === 'available';
      if (isAvailable) {
      // calculate the order count with fish price
        return prevTotal + count * fish.price;
      }
      return prevTotal;
    }, 0);
    return (
      <div className="order-wrap">
        <h2>Order</h2>
        <ul className="order"></ul>
        <div className="total">
          <strong>Total: {formatPrice(total)}</strong>
        </div>
      </div>
    );
  }
}

export default Order;
