import React from 'react';

class AddFishForm extends React.Component {
  nameRef = React.createRef();
  priceRef = React.createRef();
  statusRef = React.createRef();
  descRef = React.createRef();
  imageRef = React.createRef();
  createFish = event => {
    // Stop form from submittin
    event.preventDefault();
    // Get data for fish
    const fish = {
      name: this.nameRef,
      price: this.priceRef,
      status: this.statusRef,
      desc: this.descRef,
      image: this.imageRef
    }
    console.log(fish)
  };
  render() {
    return (
      <form className='fish-edit' onSubmit={this.createFish}>
        <input type='text' ref={this.nameRef} placeholder='name' />
        <input type='text' ref={this.priceRef} placeholder='price' />
        <select type='select' ref={this.statusRef}>
          <option value='available'>Fresh!</option>
          <option value='unavailable'>Sold Out!</option>
        </select>
        <textarea placeholder='desc' ref={this.descRef}></textarea>
        <input type='text' ref={this.imageRef} placeholder='image' />
        <button type='submit'>+ Add Fish</button>
      </form>
    );
  }
}
export default AddFishForm;
