import React from "react";
import { formatPrice } from "../helpers";

class Order extends React.Component {
  renderOrder = (key) => {
    return <li></li>;
  };
  render() {
    const orderIds = Object.keys(this.props.order);
    const total = orderIds.reduce((prevTotal, key) => {
      // get fish object by order fishe's key
      const fish = this.props.fishes[key];
      // get order count by the order fish's key
      const count = this.props.order[key];
      // check is the fish and fish status available or not
      const isAvailable = fish && fish.status === "available";
      // calculate the order total amount
      if (isAvailable) {
        return prevTotal + count * fish.price;
      }
      return prevTotal;
    }, 0);
    return (
      <div className="order-wrap">
        <h2>Order</h2>
        <ul className="order">{orderIds.map(this.renderOreder)}</ul>
        <div className="total">
          <strong>Total: {formatPrice(total)}</strong>
        </div>
      </div>
    );
  }
}

export default Order;
